function setTrueAndSubmit(n){const t=$("#simulateForm");if($.validator.unobtrusive.parse(t),t.validate(),t.valid()){const i=document.getElementById(n),r=i.previousElementSibling;r.value=!0;t.submit()}}function assignUnitCount(n){const i=n.split("_"),e=i[0]==="Group1"?1:2;let r=document.getElementById(i[0]+"_UnitCount");const t=document.getElementById(n).value;if(t===""&&(r.value=null),isInt(t)){const o="UnitDescription"+e+"_HitPoint",u=document.getElementById(o).value;if(isInt(u)){const s=parseInt(t),h=parseInt(u),f=Math.floor(s/h);r.value=f>0?f:null}}}function assignHitpoint(n){const i=n.split("_"),f=i[0]==="Group1"?1:2;let r=document.getElementById(i[0]+"_HitPoint");const t=document.getElementById(n).value;if(t===""&&(r.value=null),isInt(t)){const e="UnitDescription"+f+"_HitPoint",u=document.getElementById(e).value;if(isInt(u)){const o=parseInt(t),s=parseInt(u),h=o*s;r.value=h}}}function isInt(n){return!isNaN(n)&&parseInt(Number(n))==n&&!isNaN(parseInt(n,10))}