function setTrueAndSubmit(n){const t=$("#simulateForm");if($.validator.unobtrusive.parse(t),t.validate(),t.valid()){const i=document.getElementById(n),r=i.previousElementSibling;r.value=!0;t.submit()}}function assignUnitCount(n){const i=n.split("_"),f=i[0]==="Group1"?1:2;let r=document.getElementById(i[0]+"_UnitCount");const t=document.getElementById(n).value;if(t===""&&(r.value=null),isInt(t)){const e="UnitDescription"+f+"_HitPoint",u=document.getElementById(e).value;if(isInt(u)){const o=parseInt(t),s=parseInt(u);setUnitCountField(o,s,r)}}}function assignHitpointFromUnitCount(n){const i=n.split("_"),f=i[0]==="Group1"?1:2;let r=document.getElementById(i[0]+"_HitPoint");const t=document.getElementById(n).value;if(t===""&&(r.value=null),isInt(t)){const e="UnitDescription"+f+"_HitPoint",u=document.getElementById(e).value;if(isInt(u)){const o=parseInt(t),s=parseInt(u);r.value=o*s}}}function assignGroupHitpointFromUnitHitpoint(n){const t=n.split("_"),i=t[0]==="UnitDescription1"?1:2;assignHitpointFromUnitCount("Group"+i+"_UnitCount")}function setUnitCountField(n,t,i){if(t!==0){const r=Math.ceil(n/t);i.value=r>0?r:null}}function isInt(n){return!isNaN(n)&&parseInt(Number(n))==n&&!isNaN(parseInt(n,10))}