function setTrueAndSubmit(n){const t=$("#simulateForm");if($.validator.unobtrusive.parse(t),t.validate(),t.valid()){const i=document.getElementById(n),r=i.previousElementSibling;r.value=!0;t.submit()}}function assignUnitCount(n){const t=document.getElementById(n).value;if(isInt(t)){const i=n.split("_"),f=i[0]==="Group1"?1:2,e="UnitDescription"+f+"_HitPoint",r=document.getElementById(e).value;if(isInt(r)){const o=parseInt(t),s=parseInt(r),u=Math.floor(o/s);let h=document.getElementById(i[0]+"_UnitCount");h.value=u>0?u:null}}}function isInt(n){return!isNaN(n)&&parseInt(Number(n))==n&&!isNaN(parseInt(n,10))}