function setTrueAndSubmit(n){const t=$("#simulateForm");if($.validator.unobtrusive.parse(t),t.validate(),t.valid()){const i=document.getElementById(n),r=i.previousElementSibling;r.value=!0;t.submit()}}function assignUnitCount(n){const i=n.split("_"),f=i[0]==="Group1"?1:2;let r=document.getElementById(i[0]+"_UnitCount");const t=document.getElementById(n).value;if(t===""&&(r.value=null),isInt(t)){const e="UnitDescription"+f+"_HitPoint",u=document.getElementById(e).value;if(isInt(u)){const o=parseInt(t),s=parseInt(u);setUnitCountField(o,s,r)}}}function assignHitpoint(n){const i=n.split("_"),f=i[0]==="Group1"?1:2;let r=document.getElementById(i[0]+"_HitPoint");const t=document.getElementById(n).value;if(t===""&&(r.value=null),isInt(t)){const e="UnitDescription"+f+"_HitPoint",u=document.getElementById(e).value;if(isInt(u)){const o=parseInt(t),s=parseInt(u);r.value=o*s}}}function fillUnitCountOrHitpoint(n){const s=n.split("_"),u=s[0]==="UnitDescription1"?1:2;let f=document.getElementById("Group"+u+"_HitPoint"),e=document.getElementById("Group"+u+"_UnitCount");const i=document.getElementById(n).value;if(i!==""&&isInt(i)){const o=parseInt(i);let t=f.value,r=e.value;if(t!==""||r!==""){if(t!==""){if(!isInt(t))return;const n=parseInt(t);setUnitCountField(n,o,e);return}if(isInt(r)){const h=parseInt(r);f.value=h*o}}}}function setUnitCountField(n,t,i){if(t!==0){const r=Math.ceil(n/t);i.value=r>0?r:null}}function isInt(n){return!isNaN(n)&&parseInt(Number(n))==n&&!isNaN(parseInt(n,10))}